import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import DynamicDiv from './dynamicDiv';
import AddEntry from './addEntry';
import LoginForm from './loginForm';
import SignupForm from './signupForm';
import CurrentPage from './state_enum'
import BrowseEntries from './browseEntries'

const ScreenContent = () =>
{
    const [currentPage, setCurrentPage] = useState(CurrentPage.Login);

    const myContent = () => 
        {
            switch(currentPage) { // Different returns here
                case CurrentPage.Signup:
                    return <SignupForm/>;
                // code block
                break;
                case CurrentPage.Login:
                    return <LoginForm/>;
                // code block
                break;
                case CurrentPage.AddNew:
                    return <AddEntry/>
                break;
                case CurrentPage.Browse:
                    return <BrowseEntries/>
                break;
                default:
                    return <p>Invalid state. Can't open page.</p>

                // code block
            } 
        }

        // useEffect(() => {
