import axios from 'axios';
import { InputText } from 'primereact/inputtext';
import React, { useState, useEffect } from 'react';

import { Button } from 'primereact/button';
import { Password } from 'primereact/password';
import { Card } from 'primereact/card';
import { Toast } from 'primereact/toast';

const LoginForm = () => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);
    const toast = React.useRef(null);

    const handleSubmit = (e) => {
        e.preventDefault();
        // Example validation
        if (!username || !email || !password) {
            setError('All fields are required');
            toast.current.show({ severity: 'error', summary: 'Error', detail: error, life: 3000 });
            return;
        }
        // Add login logic here
        toast.current.show({ severity: 'success', summary: 'Success', detail: 'Login successful', life: 3000 });
    };

    return (
        <div className="login-form-container" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <Card title="Login" style={{ width: '400px' }}>
                <Toast ref={toast} />
                <form onSubmit={handleSubmit}>
                    <div className="p-field p-grid">
                        <label htmlFor="username" className="p-col-12 p-md-2">Username</label>
                        <div className="p-col-12 p-md-10">
                            <InputText id="username" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Enter your username" />
                        </div>
                    </div>
                    <div className="p-field p-grid">
                        <label htmlFor="email" className="p-col-12 p-md-2">Email</label>
                        <div className="p-col-12 p-md-10">
                            <InputText id="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Enter your email" />
                        </div>
                    </div>
                    <div className="p-field p-grid">
                        <label htmlFor="password" className="p-col-12 p-md-2">Password</label>
                        <div className="p-col-12 p-md-10">
                            <Password id="password" value={password} onChange={(e) => setPassword(e.target.value)} feedback={false} placeholder="Enter your password" />
                        </div>
                    </div>
                    <div className="p-field">
                        <Button label="Login" type="submit" icon="pi pi-sign-in" />
                    </div>
                </form>
            </Card>
        </div>
    );
};

export default LoginForm;
